contracts/PointOfSale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.5;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface COLOInterface {
  function mint(address account, uint256 rawAmount) external;
}

interface ERC20 {
  function transferFrom(
    address src,
    address dst,
    uint256 rawAmount
  ) external returns (bool);
}

interface Membership {
  function balanceOf(address account) external view returns (uint256);
}

contract PointOfSale is Ownable {
  using Counters for Counters.Counter;
  Counters.Counter public requestCount;
  string public tokenURI;
  uint256 public proposalCount;
  uint256 public constant DELAY_BETWEEN_BUYS = 30 days;
  uint256 public reward;

  struct Request {
    uint256 id;
    address seller;
    address buyer;
    uint256 amount;
    bool completed;
    address token;
    bool onEth;
  }

  COLOInterface public COLO;
  Membership public membership;

  mapping(uint256 => Request) public requests;
  mapping(address => uint256) public latestRewardDate;
  mapping(address => bool) public vendors;

  constructor(
    address _COLO,
    address _membership,
    address _governor,
    uint256 _reward
  ) {
    COLO = COLOInterface(_COLO);
    membership = Membership(_membership);
    transferOwnership(_governor);
    reward = _reward;
  }

  function setVendorStatus(address _vendor, bool status) public onlyOwner {
    vendors[_vendor] = status;
  }

  function createRequest(
    uint256 _amount,
    address _buyer,
    address _token
  ) public {
    require(vendors[msg.sender], "only vendors can create requests");
    requestCount.increment();
    Request memory newRequest =
      Request({
        id: requestCount.current(),
        seller: msg.sender,
        buyer: _buyer,
        amount: _amount,
        token: _token,
        onEth: false,
        completed: false
      });
    requests[newRequest.id] = newRequest;
  }

  function createRequestETH(uint256 _amount, address _buyer) public {
    require(vendors[msg.sender], "only vendors can create requests");
    requestCount.increment();
    Request memory newRequest =
      Request({
        id: requestCount.current(),
        seller: msg.sender,
        buyer: _buyer,
        amount: _amount,
        token: address(0),
        onEth: true,
        completed: false
      });
    requests[newRequest.id] = newRequest;
  }

  function payRequest(uint256 requestId) public {
    Request storage request = requests[requestId];
    require(!request.completed, "request must be open");
    request.completed = true;
    require(
      ERC20(request.token).transferFrom(
        msg.sender,
        request.seller,
        request.amount
      ),
      "Transfer failed"
    );
    if (
      membership.balanceOf(request.buyer) > 0 &&
      block.timestamp > latestRewardDate[request.buyer]
    ) {
      latestRewardDate[request.buyer] = block.timestamp + DELAY_BETWEEN_BUYS;
      COLO.mint(request.buyer, reward);
    }
  }

  function payRequestETH(uint256 requestId) public payable {
    Request storage request = requests[requestId];
    require(!request.completed, "request must be open");
    require(msg.value == request.amount, "amount not valid");
    request.completed = true;
    payable(request.seller).transfer(msg.value);
    if (
      membership.balanceOf(request.buyer) > 0 &&
      block.timestamp > latestRewardDate[request.buyer]
    ) {
      latestRewardDate[request.buyer] = block.timestamp + DELAY_BETWEEN_BUYS;
      COLO.mint(request.buyer, reward);
    }
  }
}

contracts/Memberships.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.5;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface COLOInterface {
  function mint(address account, uint256 rawAmount) external;
}

contract Memberships is ERC721, Ownable {
  using Counters for Counters.Counter;
  Counters.Counter private _tokenIds;
  string public tokenURI;
  uint256 public proposalCount;
  COLOInterface public COLO;

  uint256 reward = 1 ether;

  struct Proposal {
    uint256 id;
    address proposer;
    string did;
    bool accepted;
    bool revision;
  }

  mapping(uint256 => Proposal) public proposals;

  mapping(address => uint256) public latestProposalIds;

  constructor(
    string memory _tokenURI,
    address _governor,
    address _COLO
  ) ERC721("Membership", "COL") {
    tokenURI = _tokenURI;
    COLO = COLOInterface(_COLO);
    transferOwnership(_governor);
  }

  function requestMembership(string memory _did) public {
    uint256 latestProposalId = latestProposalIds[msg.sender];
    require(latestProposalId == 0, "One live proposal per proposer");
    proposalCount++;
    Proposal memory newProposal =
      Proposal({
        id: proposalCount,
        proposer: msg.sender,
        did: _did,
        accepted: false,
        revision: false
      });
    proposals[newProposal.id] = newProposal;
  }

  function denyMembership(uint256 proposalId) public onlyOwner {
    Proposal storage proposal = proposals[proposalId];
    require(balanceOf(proposal.proposer) == 0, "Can't deny an approved"); // Deny could work if we burn the membership
    proposal.revision = true;
    proposal.accepted = false;
  }

  function grantMembership(uint256 proposalId)
    public
    onlyOwner
    returns (uint256)
  {
    _tokenIds.increment();
    Proposal storage proposal = proposals[proposalId];
    require(balanceOf(proposal.proposer) == 0, "Can't mint two tokens");
    proposal.accepted = true;
    proposal.revision = true;
    uint256 newMembershipId = _tokenIds.current();
    _mint(proposal.proposer, newMembershipId);
    _setTokenURI(newMembershipId, tokenURI);
    COLO.mint(proposal.proposer, reward);
    return newMembershipId;
  }

  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 amount
  ) internal virtual override {
    super._beforeTokenTransfer(from, to, amount);
    require(from == address(0), "Token can't be transfer");
  }
}

